<body>

    <!--[if IE 8]>
    <script src="//cdnjs.cloudflare.com/ajax/libs/ie8/0.2.5/ie8.js"></script>
    <![endif]-->
    <!--[if lte IE 9]>
    <script src="https://cdn.auth0.com/js/base64.js"></script>
    <script src="https://cdn.auth0.com/js/es5-shim.min.js"></script>
    <![endif]-->

    <script src="https://cdn.auth0.com/js/lock/11.24.5/lock.min.js"></script>

    <div class="pane-container">
        <div class="pane">
            <div id="bcc-login-container">
            </div>
        </div>
    </div>
    <div class="footer">
        <div id="support-link" class="content">
        </div>
    </div>

    <script>
        var supportLink = '<a href="mailto:it@bcc.no?Subject=Login%20support%20&Body=Name%3A%0D%0A%0D%0AEmail%3A%0D%0A%0D%0ALocal%20church%3A%20%0D%0A%0D%0AMessage%3A%20%22login%20page%20support%22%0D%0A%0D%0A%0D%0APlease%20provide%20details%20about%20the%20problem%3A" target="_blank">support</a>';

        var getBrowserLanguage = function () {
            var nav = window.navigator,
                language,
                browserLanguagePropertyKeys = ['language', 'browserLanguage', 'systemLanguage', 'userLanguage'],
                AllowedLanguages = ['af', 'ca', 'cs', 'da', 'de', 'en', 'es', 'et', 'fa', 'fi', 'fr', 'hu', 'it', 'ja', 'ko', 'no', 'nn', 'nb', 'nl', 'pl', 'ro', 'ru', 'sk', 'sl', 'sv', 'tr', 'ua', 'vi', 'zh'];

            // support for HTML 5.1 "navigator.languages"
            if (Array.isArray(nav.languages)) {
                for (var i = 0; i < nav.languages.length; i++) {
                    language = nav.languages[i].substring(0, 2);
                    if (language && language.length && (AllowedLanguages.indexOf(language) >= 0)) {
                        return language;
                    }
                }
            }

            // support for other well known properties in browsers
            for (var i = 0; i < browserLanguagePropertyKeys.length; i++) {
                var languageProperty = nav[browserLanguagePropertyKeys[i]];

                if (languageProperty != undefined) {
                    language = languageProperty.substring(0, 2);

                    if (language && language.length && (AllowedLanguages.indexOf(language) >= 0)) {
                        return language;
                    }
                }
            }

            // Return english as default language
            return 'en';
        };

        function getLanguageDictionary(browserLanguage) {
            if (browserLanguage === 'nb') {
                return {
                    title: 'Development',
                    success: {
                        signUp: 'Takk for at du registrerte deg. Du har mottatt en e-post som inneholder en bekreftelseskobling. Klikk på den linken for å ferdigstille registreringen!'
                    },
                    error: {
                        signUp: {
                            'lock.fallback': "Epostadressen er ikke registrert i systemet. <br> <br>  Ta kontakt med din lokale PMO ansvarlig for å registrere din e-postadresse eller ta kontakt med " + supportLink + "."
                        }
                    },
                    welcomeMessage: '<small> Ta kontakt med ' + supportLink + ' hvis du har problemer med å logge inn</small>'
                };
            } else if (browserLanguage === 'de') {
                return {
                    title: 'Development',
                    success: {
                        signUp: 'Vielen Dank für deine Registrierung. Du hast eine E-Mail mit einem Bestätigungslink erhalten. Klicke auf diesen Link um deine Registrierung abzuschließen!'
                    },
                    error: {
                        signUp: {
                            'lock.fallback': "Die angegebene E-Mail-Adresse konnte in unserem System nicht gefunden werden. <br> <br> Bitte den PMO Verantwortlichen deiner Ortsgemeinde, deine E-Mail-Adresse zu registrieren oder wende dich an den " + supportLink + "."
                        }
                    },
                    welcomeMessage: '<small> Nimm mit dem ' + supportLink + ' Kontakt auf, wenn du Probleme beim Einloggen hast. </small>'
                };
            } else {
                return {
                    title: 'Development',
                    success: {
                        signUp: "Thanks for signing up, you have received an email containing a verification link. Click on the link to complete the registration!"
                    },
                    error: {
                        signUp: {
                            'lock.fallback': "The email address provided could not be found in our system. <br> <br> Please contact your local PMO administrator to register your email address or contact " + supportLink + "."
                        }
                    },
                    welcomeMessage: '<small>Please contact ' + supportLink + ' if you have trouble signing in</small>'
                };
            };
        }

        // Decode utf8 characters properly
        var config = JSON.parse(decodeURIComponent(escape(window.atob('@@config@@'))));
        config.extraParams = config.extraParams || {};
        var connection = config.connection;
        var prompt = config.prompt;
        var languageDictionary;
        var language = getBrowserLanguage();

        // Norwegian conversion
        if (language == 'nn' || language == 'no') language = 'nb';

        // update languageDictionary
        languageDictionary = getLanguageDictionary(language);


        var loginHint = config.extraParams.login_hint;

        var response_type = (config.internalOptions || {}).response_type || (config.internalOptions || {}).type;

        // see following for configuration options: https://auth0.com/docs/libraries/lock/v11/configuration
        var lock = new Auth0Lock(config.clientID, config.auth0Domain, {
            allowShowPassword: true,
            container: 'bcc-login-container',
            auth: {
                redirectUrl: config.callbackURL,
                responseType: response_type || (config.callbackOnLocationHash ? 'token' : 'code'),
                params: config.internalOptions
            },
            configurationBaseUrl: config.clientConfigurationBaseUrl,
            overrides: {
                __tenant: config.auth0Tenant,
                __token_issuer: config.authorizationServer.issuer
            },
            assetsUrl: config.assetsUrl,
            allowedConnections: connection ? [connection] : null,
            rememberLastLogin: !prompt,
            language: language,
            languageDictionary: languageDictionary,
            closable: false,
            autofocus: true,
          	allowSignUp: false,
            theme: {
                logo: 'https://bcc-sso.azurewebsites.net/images/BCC_logo.png',
                primaryColor: '#800080',
                authButtons: {
                    "Telegram-Prod": {
                        displayName: "Telegram",
                        primaryColor: "#9bd8fc",
                        icon: "https://a3e811289234f4ab3b16db2d.blob.core.windows.net/bcc-sso/5842a8fba6515b1e0ad75b03.png"
                    },
                    "google-oauth2": {
                        displayName: "Google",
                        primaryColor: "#dfdfdf",
                        icon: "https://a3e811289234f4ab3b16db2d.blob.core.windows.net/bcc-sso/googleg_standard_color_128dp.png"
                    },
                    "windowslive": {
                        displayName: "Microsoft",
                        primaryColor: "#dfdfdf",
                        icon: "https://a3e811289234f4ab3b16db2d.blob.core.windows.net/bcc-sso/Microsoft.png"
                    }                 
                },
                prefill: loginHint ? {
                    email: loginHint,
                    username: loginHint
                } : null,
                avatar: null,
                loginAfterSignUp: false,
                showTerms: false,
                __useTenantInfo: config.isThirdPartyClient,
                // uncomment if you want small buttons for social providers
                socialButtonStyle: 'big'
            }
        });

        // logging for sites that use the proxy
        if ((config.internalOptions || {}).audience === 'proxy.userinfo') {
            window.appInsights.trackTrace("proxyResponseTypeLogging", {
                response_type: response_type,
                state: config.internalOptions.state,
                nonce: config.internalOptions.nonce,
                clientID: config.clientID,
            });
        }

        lock.on("unrecoverable_error", function (error) {
            window.appInsights.trackException(error);
        });

        var node = document.createElement('div');
        node.innerHTML = languageDictionary.welcomeMessage;
        document.getElementById('support-link').appendChild(node);

        lock.show();
    </script>

    <style>
        html {
            height: 100%;
        }

        body {
            background: #26282b;
            height: 100%;
            display: flex;
            flex-direction: column;
            font-family: "Avenir Next", Avenir, -apple-system, BlinkMacSystemFont, Roboto, Helvetica, sans-serif;
            line-height: 1.3;
            margin: 0;
        }

        .pane-container {
            display: flex;
            flex-direction: row;
            -o-justify-content: center;
            -webkit-justify-content: center;
            justify-content: center;
            -ms-align-items: center;
            -o-align-items: center;
            -webkit-align-items: center;
            align-items: center;
            -o-flex-grow: 1;
            -webkit-flex-grow: 1;
            flex-grow: 1;
        }

        .pane {
            background: #ffffff;
            margin: 8px;
            -ms-border-radius: 10px;
            border-radius: 10px;
        }

        .footer {
            margin: 0 8px 3vh;
        }

        .footer .content {
            text-align: center;
            color: #ffffff;
            font-size: 14px;
        }



        .pane .header {
            -ms-border-radius: 8px;
            border-radius: 8px;
        }

        .pane {
            color: #757575;
        }

        .auth0-lock.auth0-lock.auth0-lock-opened .auth0-lock-widget {
            box-shadow: none;
        }

        .auth0-lock.auth0-lock .auth0-lock-overlay {
            background: #26282b;
        }

        .auth0-lock.auth0-lock .auth0-lock-header {
            border-radius: 8px 8px 0 0;
        }

        .auth0-lock.auth0-lock .auth0-lock-cred-pane {
            border-radius: 10px;
        }

        .auth0-lock.auth0-lock .auth0-lock-header-bg {
            background-color: #f5f5f5;
        }


        .auth0-lock.auth0-lock .auth0-global-message {
            font-size: 13px;
            text-transform: none;
        }

        .auth0-lock.auth0-lock .auth0-global-message.auth0-global-message-success {
            background: #2ECC71;
        }

        .auth0-lock.auth0-lock .auth0-global-message.auth0-global-message-error {
            background: #FF3E00;
        }

        a, a:hover,
        .auth0-lock.auth0-lock a, .auth0-lock.auth0-lock .auth0-lock-form .auth0-lock-alternative a.auth0-lock-alternative-link {
            color: #2ecc71;
            text-decoration: underline;
        }

        a:hover {
            -ms-opacity: 0.5;
            opacity: 0.5;
        }

        .auth0-lock.auth0-lock .auth0-lock-social-button {
            border-radius: 40px;
        }

        .auth0-lock.auth0-lock button.auth0-lock-submit {
            border-radius: 0 0 8px 8px;
            font-weight: bold;
            text-transform: unset;
            font-size: 12pt;
        }

        .auth0-lock.auth0-lock .auth0-lock-social-button.auth0-lock-social-big-button {
            display: inline-block;
            margin-right: 2px;
            margin-left: 2px;
            width: 40px;
        }

        .auth0-lock.auth0-lock .auth0-lock-social-button.auth0-lock-social-big-button .auth0-lock-social-button-text {
            display: none;
        }

        .auth0-lock.auth0-lock .auth-lock-social-buttons-pane {
            margin: 0 -10px;
        }

        .auth0-lock.auth0-lock .auth0-lock-social-button[data-provider^=google].auth0-lock-social-big-button .auth0-lock-social-button-icon {
          	background-size: 70%;
            margin-top: 1px;
        }
        .auth0-lock.auth0-lock .auth0-lock-social-button[data-provider="oauth2"].auth0-lock-social-big-button .auth0-lock-social-button-icon {
          	background-size: 100%;
        }
    </style>
</body>

</html>
